    private void equalButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_equalButtonActionPerformed
       String trans = new calculator.logic.TranslateToPostfix(jTextField1.getText()).doTrans();
       displayLabel.setText(String.format("%.1f", new calculator.logic.PostfixEvalute(trans).doRes()));
        jTextField1.setText("");
    }//GEN-LAST:event_equalButtonActionPerformed

